insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (1,1,'sheetid','select a.Eid,a.Sheetid,a.CoEid,b.EName coename,a.PayType,a.SendType, a.DiscValue,a.CrmValue,a.PurValue,(a.PurValue-a.DiscValue-a.CrmValue) as SumMoney,a.PurQty,a.Status, a.FromId,a.EditDate,a.Notes,a.Editor,a.Checker,a.CheckDate, a.DeliveryDate,a.DeliveryAddress,a.Contact,a.ContactMobile,a.Operator,a.Status,d.MobilePhone,b.Manager,case when b.ManagerTele='''' then b.ManagerMobile else b.ManagerTele end ManagerTele,b.ManagerMobile,b.Contact,b.ContactTele ,(select SUM(OldBulkPrice*BulkQty+OldPackPrice*packqty) From t_Ven_SaleItem f where f.eid=a.EId and f.SheetId=a.SheetID) Oldsummoney,(select SUM(BulkQty+giftqty)-SUM(convert(int,(BulkQty+giftqty)/c.packunitqty)*c.packunitqty) from t_Ven_SaleItem c where c.sheetid=a.sheetid and c.eid=a.eid) totBulkQty,(select SUM(BulkQty+packqty*c.packunitqty) from t_Ven_SaleItem c where c.sheetid=a.sheetid and c.eid=a.eid) sumtotBulkQty  from t_sys_Org b,t_ven_Sale a LEFT JOIN t_sys_user d ON a.Eid=d.eid AND d.UserType=2 AND d.STATUS=1 AND a.Operator=d.RealName Where a.eid=? and a.Sheetid=? and a.coEid=b.Eid ')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (2,2,'sheetid','Select a.Eid,d.sheetid,a.Goodsid,a.GoodsName,a.myGoodsid,a.HelpCode,a.Barcode,a.uName,a.spec,a.PackUnit,a.PackSpec,d.PackUnitQty, d.BulkPrice,d.PackPrice,D.PackQty,d.BulkQty,d.DiscValue, d.GiftQty,a.SalePack,a.MinOrderQty, c.StockQty,d.PromotionSheetID,d.PreferNotes,d.Notes,a.PicVersion,b.BrandName,d.GiftFlag,d.StkQty as StockQty,(D.BulkPrice*D.BulkQty+D.PackPrice*D.packqty-d.DiscValue) AS SumMoney,(D.BulkQty+D.packqty*d.PackUnitQty) AS SumQty from V_Ven_PurchaseItem d,t_bas_mygoods a Left Outer join t_Ven_StockQty c on a.Eid=c.Eid and a.goodsid=c.Goodsid Left Outer join t_bas_brand b ON b.BrandId=a.BrandId Where ((d.coEid=a.Eid and d.coEid>10000)or (d.Eid=a.EId and d.coEid<10000)) and d.Goodsid=a.Goodsid order by d.LineNum,d.sortid,d.goodsid')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (2,1,'sheetid','  select a.Eid,a.Sheetid,a.CoEid,case when a.coeid<1000 then e.vendname  else b.EName end coename,a.PayType,a.SendType, a.DiscValue,a.CrmValue,a.PurValue,(a.PurValue-a.DiscValue-a.CrmValue) as SumMoney,a.PurQty,a.Status, a.FromId,a.EditDate,a.Notes,a.Editor,a.Checker,a.CheckDate, a.DeliveryDate,a.DeliveryAddress,a.Contact,a.ContactMobile,a.Operator,a.Status,d.MobilePhone,b.Manager,case when b.ManagerTele='''' then b.ManagerMobile else b.ManagerTele end ManagerTele,b.ManagerMobile,b.Contact,b.ContactTele,a.PurQty totPackQty,(select SUM(BulkQty+giftqty)-SUM(convert(int,(BulkQty+giftqty)/c.packunitqty)*c.packunitqty) from t_Ven_PurchaseItem c where c.sheetid=a.sheetid and c.eid=a.eid) totBulkQty from t_sys_Org b,t_ven_Purchase a LEFT JOIN t_sys_user d ON a.Eid=d.eid AND d.UserType=2 AND d.STATUS=1 AND a.Operator=d.RealName  left join t_bas_vender e on e.eid=a.eid and e.coeid=a.coeid Where a.eid=? and a.Sheetid=? and a.coEid=b.Eid')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (3,1,'sheetid','select a.Eid,a.Sheetid,a.CoEid,b.EName coename,a.PayType,a.SendType, a.DiscValue,a.CrmValue,a.PurValue,(a.PurValue-a.DiscValue-a.CrmValue) as SumMoney,a.PurQty,a.Status, a.FromId,a.EditDate,a.Notes,a.Editor,a.Checker,a.CheckDate, a.DeliveryDate,a.DeliveryAddress,a.Contact,a.ContactMobile,a.Operator,a.Status,d.MobilePhone,b.Manager,case when b.ManagerTele='''' then b.ManagerMobile else b.ManagerTele end ManagerTele,b.ManagerMobile,b.Contact,b.ContactTele ,(select SUM(BulkQty+giftqty)-SUM(convert(int,(BulkQty+giftqty)/c.packunitqty)*c.packunitqty) from t_Ven_RetSaleItem c where c.sheetid=a.sheetid and c.eid=a.eid) totBulkQty,a.purqty from t_sys_Org b,t_Ven_RetSale a LEFT JOIN t_sys_user d ON a.Eid=d.eid  AND a.Operator=d.RealName Where a.eid=? and a.Sheetid=? and a.coEid=b.Eid')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (3,2,'sheetid','Select a.Eid,d.sheetid,a.Goodsid,a.GoodsName,a.myGoodsid,a.HelpCode,a.Barcode,a.uName,a.spec,a.PackUnit,a.PackSpec,d.PackUnitQty, d.BulkPrice,d.PackPrice,D.PackQty,d.BulkQty,d.DiscValue, d.GiftQty,a.SalePack,a.MinOrderQty, c.StockQty,d.PromotionSheetID,d.PreferNotes,d.Notes,a.PicVersion,b.BrandName,d.GiftFlag,(D.BulkPrice*D.BulkQty+D.PackPrice*D.packqty-d.DiscValue) AS SumMoney from t_Ven_RetSaleItem d,t_bas_mygoods a Left Outer join t_Ven_StockQty c on a.Eid=c.Eid and a.goodsid=c.Goodsid Left Outer join t_bas_brand b ON b.BrandId=a.BrandId Where d.Eid=a.Eid and d.Goodsid=a.Goodsid order by d.LineNum')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (4,1,'sheetid','select a.Eid,a.Sheetid,a.CoEid,  case when a.coeid<1000 then e.VendName else b.ename end coename,a.PayType,a.SendType, a.DiscValue,a.CrmValue,a.PurValue,(a.PurValue-a.DiscValue-a.CrmValue) as SumMoney,a.PurQty,a.Status, a.FromId,a.EditDate,a.Notes,a.Editor,a.Checker,a.CheckDate, a.DeliveryDate,a.DeliveryAddress,a.Contact,a.ContactMobile,a.Operator,a.Status,d.MobilePhone   from t_sys_Org b,t_Ven_RetPurchase a   LEFT JOIN t_sys_user d ON a.Eid=d.eid AND d.UserType=2 AND d.STATUS=1 AND a.Operator=d.RealName   Left join t_bas_vender e on a.eid=e.eid and a.coeid=e.coeid  Where a.eid=? and a.Sheetid=? and a.coEid=b.Eid ')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (4,2,'sheetid','Select a.Eid,d.sheetid,a.Goodsid,a.GoodsName,a.myGoodsid,a.HelpCode,a.Barcode,a.uName,a.spec,a.PackUnit,a.PackSpec,d.PackUnitQty, d.BulkPrice,d.PackPrice,D.PackQty,d.BulkQty,d.DiscValue, d.GiftQty,a.SalePack,a.MinOrderQty, c.StockQty,d.PromotionSheetID,d.PreferNotes,d.Notes,a.PicVersion,b.BrandName,d.GiftFlag,(D.BulkPrice*D.BulkQty+D.PackPrice*D.packqty-d.DiscValue) AS SumMoney from t_Ven_RetPurchaseItem d,t_bas_mygoods a Left Outer join t_Ven_StockQty c on a.Eid=c.Eid and a.goodsid=c.Goodsid Left Outer join t_bas_brand b ON b.BrandId=a.BrandId Where d.Eid=a.Eid and d.Goodsid=a.Goodsid order by d.LineNum')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (5,1,'sheetid','select A.EId,d.EName,A.SheetID,Case when A.Sheettype=1001 then ''采购收货'' when a.Sheettype=1002 then ''采购退货'' when a.Sheettype=2001 then ''销售出库'' when a.Sheettype=2002 then ''销售退货'' when a.Sheettype=3001 then ''一般出库'' when a.Sheettype=3002 then ''一般入库'' when a.Sheettype=4001 then ''试销出库'' else ''未知类型'' end Sheettype,A.RefSheetid,A.CoEId,c.EName coEname,A.StockId,b.StockName,A.OperNotes,A.Notes,A.Status,A.PrintNum,A.Operator,A.Editor,A.EditDate,A.Checker,A.CheckDate  from t_sys_org d,t_Stk_input A  Left outer join t_Bas_Stock b on a.EId=b.EId and a.StockId=b.StockId Left outer join t_Sys_Org c on a.CoEId=c.EId where d.EId=a.EId and a.Sheetid=?')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (5,2,'sheetid','Select a.SheetId,a.LineNum,a.GoodsID,b.GoodsName,b.Spec,b.UName,a.PackUnitQty,b.PackUnit,a.BatchId,a.Cost,a.BulkPrice,a.PackPrice, a.AskPackQty,a.AskBulkQty,a.AskPackQty*a.PackUnitQty+a.AskBulkQty AskTotQty,a.PackQty,a.BulkQty,a.PackQty*a.PackUnitQty+a.BulkQty TotQty,a.Notes From t_Stk_InputItem a,t_Bas_MyGoods b where a.EId=b.EId and a.GoodsID=b.GoodsID')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (7,2,'sheetid',' Select a.SheetId,a.LineNum,a.GoodsID,b.barcode,b.GoodsName,b.Spec,b.UName,a.PackUnitQty,b.PackUnit,a.BatchId,a.BulkPrice,a.PackPrice, a.OutPackQty,a.OutBulkQty,a.OutPackQty*a.PackUnitQty+a.OutBulkQty OutTotQty,a.inPackQty,a.inBulkQty,a.inPackQty*a.PackUnitQty+a.inBulkQty inTotQty,a.Notes,   isnull((select cost from t_acc_inventory a1,t_stk_transfer b1 where b1.SheetID=a.sheetid and a1.StockId=b1.OutStockId and a1.EId=a.EId and a1.Goodsid=a.goodsid),0) Cost,   isnull((select round(cost,2) from t_acc_inventory a1,t_stk_transfer b1 where b1.SheetID=a.sheetid and a1.StockId=b1.OutStockId and a1.EId=a.EId and a1.Goodsid=a.goodsid),0)*(a.OutPackQty*a.PackUnitQty+a.OutBulkQty) Costvalue   From  t_Stk_TransferItem a ,t_Bas_MyGoods b where a.EId=b.EId and a.GoodsID=b.GoodsID')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (8,1,'sheetid','select A.EId,d.EName,A.SheetID,Case when A.PDType=0 then ''全场盘点''  else ''单品盘点'' end PDType,A.StockId,b.StockName,A.Notes,A.Status,A.PrintNum,A.Operator,A.Editor,A.EditDate,A.Checker,A.CheckDate  from t_sys_org d,t_stk_pdyk A  Left outer join t_Bas_Stock b on a.EId=b.EId and a.StockId=b.StockId  where d.EId=a.EId and a.Sheetid=?')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (6,1,'sheetid','select A.EId,d.EName,A.SheetID,Case when A.Sheettype=1001 then ''采购收货'' when a.Sheettype=1002 then ''采购退货'' when a.Sheettype=2001 then ''销售出库'' when a.Sheettype=2002 then ''销售退货'' when a.Sheettype=3001 then ''一般出库'' when a.Sheettype=3002 then ''一般入库'' when a.Sheettype=4001 then ''试销出库'' else ''未知类型'' end Sheettype,A.RefSheetid,A.CoEId,c.EName coEName,A.StockId,b.StockName,A.OperNotes,A.Notes,A.Status,A.PrintNum,A.Operator,A.Editor,A.EditDate,A.Checker,A.CheckDate,(Select Sum(AskPackQty) From t_stk_OutPutitem e where e.sheetid=a.sheetid and e.eid=a.eid) PurQty,(Select Sum(askPackQty*PackPrice+askBulkQty*BulkPrice) From t_stk_OutPutitem e where e.sheetid=a.sheetid and e.eid=a.eid) PurValue,(Select Sum(askPackQty*PackPrice+askBulkQty*BulkPrice) From t_stk_OutPutitem e where e.sheetid=a.sheetid and e.eid=a.eid) summoney,c.manager,  case when c.ManagerTele='''' then c.managerMobile else c.ManagerTele end ManagerTele,c.managerMobile    from t_sys_org d,t_Stk_output A  Left outer join t_Bas_Stock b on a.EId=b.EId and a.StockId=b.StockId Left outer join t_Sys_Org c on a.CoEId=c.EId where d.EId=a.EId and a.Sheetid=?')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (7,1,'sheetid',' select A.EId,d.EName,A.SheetID,A.OutStockId,b.StockName OutStockName,a.inStockId,e.StockName InStockName,A.OperNotes,A.Notes,A.Status,A.PrintNum,A.Operator,A.Editor,A.EditDate,A.Checker,A.CheckDate, isnull((select sum(round(a1.Cost*(b1.OutPackQty*b1.PackUnitQty+b1.OutBulkQty),2)) from t_acc_inventory a1,t_Stk_TransferItem b1  where b1.SheetID=a.sheetid and a1.StockId=a.OutStockId and a1.EId=a.EId and a1.Goodsid=b1.goodsid),0) Costvalue, isnull((select sum(round(a1.Cost*(b1.OutPackQty*b1.PackUnitQty+b1.OutBulkQty),2)) from t_acc_inventory a1,t_Stk_TransferItem b1  where b1.SheetID=a.sheetid and a1.StockId=a.OutStockId and a1.EId=a.EId and a1.Goodsid=b1.goodsid),0) summoney  from t_sys_org d,t_Stk_Transfer A   Left outer join t_Bas_Stock b on a.EId=b.EId and a.OutStockId=b.StockId  Left Outer join t_bas_Stock e on e.EId=a.EId and a.InStockId=e.StockId Where a.Eid=d.Eid and a.Sheetid=?')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (8,2,'sheetid',' Select a.SheetId,a.LineNum,a.GoodsID,b.GoodsName,b.Spec,b.UName,a.PackUnitQty,b.PackUnit,a.BatchId,a.BulkPrice,a.PackPrice,a.StkPackQty,a.StkBulkQty,a.PDPackQty,a.PDBulkQty,a.PDPackQty - a.StkPackQty YKPackQty,a.PDBulkQty-a.StkBulkQty YKBulkQty,(a.PDPackQty - a.StkPackQty)*a.PackUnitQty+(a.PDBulkQty-a.StkBulkQty) YKTotQty, a.Notes From t_Stk_PdykItem a,t_Bas_MyGoods b where a.EId=b.EId and a.GoodsID=b.GoodsID')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (1,2,'sheetid','Select a.Eid,d.sheetid,a.Goodsid,a.GoodsName,a.myGoodsid,a.HelpCode,a.Barcode,a.uName,a.Spec,a.PackUnit,a.PackSpec,d.PackUnitQty,rtrim(convert(dec(12,2), d.BulkPrice)) bulkprice,rtrim(convert(dec(12,2),d.PackPrice)) packprice,case when d.bulkqty=0 then ''-'' else rtrim(convert(dec(12,2), d.BulkPrice)) end BulkPrice1,case when d.packqty=0 then ''-'' else rtrim(convert(dec(12,2),d.PackPrice)) end PackPrice1,D.PackQty,d.BulkQty,d.DiscValue, d.GiftQty,a.SalePack,a.MinOrderQty, c.StockQty,d.PromotionSheetID,d.PreferNotes,d.Notes,a.PicVersion,b.BrandName,d.GiftFlag,d.StkQty as StockQty,(D.BulkQty+D.PackUnitQty*D.packqty) AS SumQty,rtrim(convert(dec(12,2),D.BulkPrice*D.BulkQty+D.PackPrice*D.packqty-d.DiscValue)) AS SumMoney,rtrim(convert(dec(12,2),d.oldBulkPrice)) oldBulkPrice,rtrim(convert(dec(12,2),d.OldPackPrice)) oldPackPrice,rtrim(convert(dec(12,2),D.OldBulkPrice*D.BulkQty+D.OldPackPrice*D.packqty)) AS OldSumMoney,case when d.BulkPrice=d.oldBulkPrice then ''-'' else convert(varchar,convert(dec(12,2),d.BulkPrice)) end PromBulkPrice,case when d.PackPrice=d.OldPackPrice then ''-'' else convert(varchar,convert(dec(12,2),d.PackPrice)) end PromPackPrice from V_Ven_SaleItem d,t_bas_mygoods a Left Outer join t_Ven_StockQty c on a.Eid=c.Eid and a.goodsid=c.Goodsid Left Outer join t_bas_brand b ON b.BrandId=a.BrandId Where d.Eid=a.Eid and d.Goodsid=a.Goodsid order by d.LineNum,d.sortid,d.goodsid ')
insert t_sys_rptptlsql(PtlId,ptlType,KeyField,ExecSql) values (6,2,'sheetid','Select a.SheetId,ROW_NUMBER() over(order by b.mycatid,b.Goodsname) as LineNum,a.GoodsID,b.barcode,b.GoodsName,b.Spec,b.UName,a.PackUnitQty,b.PackUnit,a.BatchId,a.Cost,a.BulkPrice,a.PackPrice, a.AskPackQty,a.AskBulkQty,a.AskPackQty*a.PackUnitQty+a.AskBulkQty AskTotQty,a.PackQty,a.BulkQty,a.PackQty*a.PackUnitQty+a.BulkQty TotQty,a.PackQty*a.PackPrice+a.BulkQty*a.BulkPrice PurValue,a.Notes,  a.AskPackQty*a.PackPrice+a.AskBulkQty*a.BulkPrice askpurvalue   From t_Stk_OutputItem a,t_Bas_MyGoods b where a.EId=b.EId and a.GoodsID=b.GoodsID order by 2 ')